sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var RenderValue = x_snc_devtools.RenderValue;
	
	AppSetProperty('active','true'); 
	AppSetProperty('tables.incident.active','true'); 
	AppSetProperty('tables.incident.columns.whitelist_trigger.sys_user_group',''); 
	AppSetProperty('tables.incident.columns.instant_access.sys_user','opened_by,caller_id'); 
	
	
	var testdata = new DataSilosTestDataAPI('_ATF_DATASILOS_');

	var strUserSpectator = testdata.CreateUser('SPECTATOR',[]);
	var strUserCaller = testdata.CreateUser('CALLER',['itil']);
	var strUserItil = testdata.CreateUser('ITIL',['itil']);
	var strUserIncidentRead = testdata.CreateUser('INCIDENT_READ',['sn_incident_read']);
	
	var strIncident = testdata.CreateIncident('TEST','CALLER','CALLER','');

	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.Log('\n\nBusiness Rule and ACL is active');
	test.TestCase_IsRecordAccessibleByUser(strUserSpectator,'incident','',strIncident,false);
	test.TestCase_IsRecordAccessibleByUser(strUserCaller,'incident','',strIncident,true);
	test.TestCase_IsRecordAccessibleByUser(strUserItil,'incident','',strIncident,false);
	test.TestCase_IsRecordAccessibleByUser(strUserIncidentRead,'incident','',strIncident,false);


	test.Log('Business Rule is inactive and ACL is active');
	x_snc_devtools.RecordSetValue('sys_script','a9b3e7301b946810a89720a8ec4bcb98','active',false);
	test.Log(x_snc_devtools.GetRecord('sys_script','a9b3e7301b946810a89720a8ec4bcb98'));
	test.TestCase_IsRecordAccessibleByUser(strUserSpectator,'incident','',strIncident,false);
	test.TestCase_IsRecordAccessibleByUser(strUserCaller,'incident','',strIncident,true);
	test.TestCase_IsRecordAccessibleByUser(strUserItil,'incident','',strIncident,false);	
	test.TestCase_IsRecordAccessibleByUser(strUserIncidentRead,'incident','',strIncident,false);
	
	test.Log('\n\nBusiness Rule is inactive and ITIL ACL does NOT apply DataSilos filtering.');

	var grACL = GetDataSilosACL('incident','');
	if (grACL != false)
	{
		test.Log('\n\nBusiness Rule is inactive and DataSilos ACLs do NOT apply DataSilos filtering.');
		x_snc_devtools.RecordSetValue('sys_security_acl',grACL.sys_id,'script','answer = true;',true);
		test.Log(grACL);
		test.TestCase_IsRecordAccessibleByUser(strUserSpectator,'incident','',strIncident,false);
		test.TestCase_IsRecordAccessibleByUser(strUserCaller,'incident','',strIncident,true);
		test.TestCase_IsRecordAccessibleByUser(strUserItil,'incident','',strIncident,true);	
		test.TestCase_IsRecordAccessibleByUser(strUserIncidentRead,'incident','',strIncident,true);
	}
		
	test.Log(RenderValue(testdata.GetAllData(),true));
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
